import React from "react";
import { Card, Button } from "react-bootstrap";

const NewsCard = ({ title, description, url, source, image, published_date }) => {
    return (
        <Card className="mb-3 news-card shadow-sm">
            {image && (
                <Card.Img 
                    variant="top" 
                    src={image} 
                    className="news-card-img"
                />
            )}
            <Card.Body>
                <Card.Title className="news-title">{title}</Card.Title>
                <Card.Text className="news-description">
                    {description.length > 100 ? description.substring(0, 100) + "..." : description}
                </Card.Text>
                <div className="d-flex justify-content-between align-items-center">
                    <div>
                        <Card.Text className="text-muted small mb-1">
                            {new Date(published_date).toLocaleDateString()}
                        </Card.Text>
                        <Card.Text className="text-muted small">
                            {source}
                        </Card.Text>
                    </div>
                    <Button variant="danger" href={url} target="_blank" size="sm">
                        Read
                    </Button>
                </div>
            </Card.Body>
        </Card>
    );
};

export default NewsCard;
