// src/pages/Contact.js
import React from "react";
import { Form, Button, Container, Card } from "react-bootstrap";
import "../custom.css"; // Ensure you have this for additional styles

const Contact = () => {
    return (
        <Container className="d-flex justify-content-center align-items-center min-vh-100">
            <Card className="contact-card p-4 shadow-lg">
                <h2 className="text-center mb-4 text-primary">Get in Touch</h2>
                <p className="text-center text-muted">Weâ€™d love to hear from you! Fill out the form below.</p>

                <Form>
                    <Form.Group controlId="formName" className="mb-3">
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" placeholder="Enter your name" className="rounded-pill" />
                    </Form.Group>

                    <Form.Group controlId="formEmail" className="mb-3">
                        <Form.Label>Email Address</Form.Label>
                        <Form.Control type="email" placeholder="Enter your email" className="rounded-pill" />
                    </Form.Group>

                    <Form.Group controlId="formMessage" className="mb-3">
                        <Form.Label>Message</Form.Label>
                        <Form.Control as="textarea" rows={4} placeholder="Write your message here..." className="rounded" />
                    </Form.Group>

                    <Button variant="primary" type="submit" className="w-100 rounded-pill btn-lg">
                        Send Message ðŸš€
                    </Button>
                </Form>
            </Card>
        </Container>
    );
};

export default Contact;
