import React, { useState } from "react";
import { Search, TrendingUp, AlertCircle, Loader } from "lucide-react";

const Home = () => {
  const [query, setQuery] = useState("");
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [sentimentScore, setSentimentScore] = useState(null);
  const [recommendations, setRecommendations] = useState([]);

  const handleSearch = async () => {
    if (!query.trim()) {
      setError("Please enter a keyword.");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const data = await fetchNewsWithSentiment(query);
      setNews(data.articles);
      setSentimentScore(data.sentiment_score);
      setRecommendations(data.recommendations);
    } catch (err) {
      setError("Failed to fetch data. Please try again.");
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-black py-16 px-4">
      {/* Hero Section */}
      <div className="max-w-4xl mx-auto text-center mb-12">
        <h1 className="text-4xl md:text-5xl font-bold mb-6 bg-gradient-to-r from-[#eb5d3a] to-[#ff5733] bg-clip-text text-transparent">
          AI-Powered Market Insights: Trends & Sentiment in Real-Time
        </h1>
        <p className="text-gray-300 text-lg mb-8 flex items-center justify-center gap-2">
          <TrendingUp className="text-[#eb5d3a]" />
          Stay ahead of market shifts with real-time sentiment analysis, data-driven decisions, mitigate risks, and capitalize on emerging opportunities.
        </p>

        {/* Search Section */}
        <div className="relative max-w-2xl mx-auto">
          <div className="relative flex items-center">
            <div className="absolute left-4">
              <Search className="h-5 w-5 text-gray-400" />
            </div>
            <input
              type="text"
              className="w-full bg-[#222] text-white pl-12 pr-4 py-4 rounded-l-full focus:outline-none focus:ring-2 focus:ring-[#eb5d3a] border-0"
              placeholder="Search for trends (e.g., AI, Finance, Stocks)..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            />
            <button
              onClick={handleSearch}
              className="bg-gradient-to-r from-[#eb5d3a] to-[#ff5733] text-white px-8 py-4 rounded-r-full font-semibold hover:opacity-90 transition-all duration-300 flex items-center gap-2"
            >
              Analyze
              {loading && <Loader className="animate-spin" />}
            </button>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="mt-6 flex items-center justify-center gap-2 text-red-500">
            <AlertCircle className="h-5 w-5" />
            <p>{error}</p>
          </div>
        )}

        {/* Sentiment Score */}
        {sentimentScore !== null && (
          <div className="mt-12 bg-[#1a1a1a] rounded-2xl p-8 max-w-2xl mx-auto">
            <h2 className="text-2xl font-semibold mb-6 text-white">Market Sentiment Analysis</h2>
            <div className="h-4 bg-[#333] rounded-full overflow-hidden">
              <div
                className="h-full bg-gradient-to-r from-[#eb5d3a] to-[#ff5733] transition-all duration-500"
                style={{ width: `${Math.max(Math.min(sentimentScore * 100, 100), 0)}%` }}
              />
            </div>
            <p className="mt-4 text-gray-300">{recommendations[0]}</p>
          </div>
        )}

        {/* News Grid */}
        {news.length > 0 && (
          <div className="mt-12 grid md:grid-cols-2 gap-6">
            {news.map((article, index) => (
              <div
                key={index}
                className="bg-[#1a1a1a] rounded-xl p-6 hover:transform hover:-translate-y-2 transition-all duration-300"
              >
                {article.urlToImage && (
                  <img
                    src="/api/placeholder/400/200"
                    alt={article.title}
                    className="w-full h-48 object-cover rounded-lg mb-4"
                  />
                )}
                <h3 className="text-xl font-semibold mb-2 text-white">{article.title}</h3>
                <p className="text-gray-400 mb-4">{article.description}</p>
                <div className="flex justify-between items-center">
                  <span className="text-[#eb5d3a]">{article.source.name}</span>
                  <button className="text-white hover:text-[#eb5d3a] transition-colors">Read More →</button>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* No Results Message */}
        {!loading && news.length === 0 && query && (
          <p className="mt-8 text-gray-400">No news found. Try another keyword.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
