import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form } from 'react-bootstrap';
import './custom.css';

const Home = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [newsArticles, setNewsArticles] = useState([]);
    const [sentimentScore, setSentimentScore] = useState(null);

    useEffect(() => {
        fetchNews();
    }, []);

    const fetchNews = async () => {
        try {
            const response = await fetch(`https://api.bing.com/news/search?q=${searchQuery}`);
            const data = await response.json();
            setNewsArticles(data.articles);
            analyzeSentiment(data.articles);
        } catch (error) {
            console.error('Error fetching news:', error);
        }
    };

    const analyzeSentiment = async (articles) => {
        try {
            const response = await fetch('https://api.ibmcloud.com/sentiment-analysis', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ text: articles.map(article => article.title).join('. ') })
            });
            const data = await response.json();
            setSentimentScore(data.sentiment);
        } catch (error) {
            console.error('Error analyzing sentiment:', error);
        }
    };

    return (
        <Container className="mt-4">
            <Row className="justify-content-center">
                <Col md={8} className="text-center">
                    <h1 className="mb-4">News Analyzer</h1>
                    <Form onSubmit={(e) => { e.preventDefault(); fetchNews(); }}>
                        <Form.Control 
                            type="text" 
                            placeholder="Search for news..." 
                            value={searchQuery} 
                            onChange={(e) => setSearchQuery(e.target.value)} 
                            className="mb-3 search-bar"
                        />
                        <Button variant="primary" type="submit">Search</Button>
                    </Form>
                </Col>
            </Row>
            
            <Row className="justify-content-center my-4">
                <Col md={6} className="text-center">
                    <h3>Sentiment Meter</h3>
                    <div className="sentiment-meter">
                        <div className="thermometer" style={{ height: `${sentimentScore * 100}%`, backgroundColor: sentimentScore > 0.5 ? 'red' : 'blue' }}></div>
                    </div>
                    <p>{sentimentScore !== null ? `Sentiment Score: ${sentimentScore.toFixed(2)}` : 'Fetching sentiment...'}</p>
                </Col>
            </Row>
            
            <Row>
                {newsArticles.map((article, index) => (
                    <Col md={4} key={index} className="mb-4">
                        <Card className="news-card">
                            <Card.Img variant="top" src={article.image} alt="News" />
                            <Card.Body>
                                <Card.Title>{article.title}</Card.Title>
                                <Card.Text>{article.description}</Card.Text>
                                <Button variant="primary" href={article.url} target="_blank">Read More</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
        </Container>
    );
};

export default Home;
