import React from "react";
import { Card, Button } from "react-bootstrap";

const NewsCard = ({ title, description, url, source, image, published_date }) => {
    return (
        <Card className="mb-4 shadow-lg">
        {image && <Card.Img variant="top" src={image} />}
        <Card.Body>
            <Card.Title>{title}</Card.Title>
            <Card.Text>{description}</Card.Text>
            <Card.Text className="text-muted small">Published: {new Date(published_date).toLocaleDateString()}</Card.Text>
            <Card.Text className="text-muted small">Source: {source}</Card.Text>
            <Button variant="primary" href={url} target="_blank">
            Read More
            </Button>
        </Card.Body>
    </Card>
    );
};

export default NewsCard;
