import axios from "axios";

const API_BASE_URL = "http://127.0.0.1:5000/news"; // Update if needed

export const fetchNews = async (query) => {
    try {
        const response = await axios.get(`${API_BASE_URL}/fetch-news?query=${query}`);
        return response.data.articles;
    } catch (error) {
    console.error("Error fetching news:", error);
    return [];
}
};
