import React from "react";
import { RadialBarChart, RadialBar, PolarAngleAxis } from "recharts";

const SentimentMeter = ({ score }) => {
    const normalizedScore = Math.max(-1, Math.min(1, Number(score) || 0));

    // Convert sentiment score (-1 to 1) to a 0-100 range for better visualization
    const percentage = ((normalizedScore + 1) * 50).toFixed(1);

    // Define color logic for different sentiment ranges
    const getColor = (value) => {
        if (value > 70) return "#28a745";  // Green - Positive
        if (value < 30) return "#dc3545";  // Red - Negative
        return "#ffc107";  // Yellow - Neutral
    };

    // Data for Radial Bar Chart
    const data = [{ value: percentage, fill: getColor(percentage) }];

    return (
        <div className="sentiment-meter text-center">
            <RadialBarChart 
                width={250} 
                height={150} 
                cx={125} 
                cy={150} 
                innerRadius={50} 
                outerRadius={100} 
                startAngle={180} 
                endAngle={0} 
                barSize={15} 
                data={data}
            >
                <PolarAngleAxis 
                    type="number" 
                    domain={[0, 100]} 
                    angleAxisId={0} 
                    tick={false} 
                />
                <RadialBar 
                    background={{ fill: "#ddd" }} 
                    dataKey="value" 
                />
            </RadialBarChart>
            <h6 className="mt-2" style={{ color: getColor(percentage) }}>
                {normalizedScore > 0.3 ? "Positive" : normalizedScore < -0.3 ? "Negative" : "Neutral"}
            </h6>
        </div>
    );
};

export default SentimentMeter;
