import React from "react";
import { Card, Button } from "react-bootstrap";

const NewsCard = ({ title, description, url, source, image, published_date }) => {
    const truncateText = (text, maxLength) => {
        if (!text) return '';
        return text.length > maxLength ? text.slice(0, maxLength) + '...' : text;
    };

    return (
        <div className="col-md-4 mb-4">
            <Card className="modern-news-card h-100">
                {image && (
                    <div className="card-img-wrapper">
                        <Card.Img 
                            variant="top" 
                            src={image} 
                            className="card-img"
                        />
                    </div>
                )}
                <Card.Body className="d-flex flex-column">
                    <Card.Title className="news-title mb-3">
                        {truncateText(title, 80)}
                    </Card.Title>
                    <Card.Text className="news-description mb-4">
                        {truncateText(description, 160)}
                    </Card.Text>
                    <div className="news-footer mt-auto">
                        <div className="news-meta">
                            <small className="text-muted d-block">
                                {new Date(published_date).toLocaleDateString()}
                            </small>
                            <small className="text-muted d-block source-text">
                                {source}
                            </small>
                        </div>
                        <Button 
                            variant="dark" 
                            href={url} 
                            target="_blank"
                            className="read-more-btn"
                        >
                            Read More
                        </Button>
                    </div>
                </Card.Body>
            </Card>
        </div>
    );
};

export default NewsCard;
