import React, { useState } from "react";
import { fetchNews } from "../services/api";
import NewsCard from "../components/NewsCard";
import { Container, Form, Button, Row, Col, Spinner, Alert } from "react-bootstrap";

const Home = () => {
    const [query, setQuery] = useState("");
    const [news, setNews] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleSearch = async () => {
        if (!query.trim()) {
        setError("Please enter a keyword.");
        return;
        }

    setLoading(true);
    setError("");
    const data = await fetchNews(query);
    setNews(data);
    setLoading(false);
};

return (
    <Container className="mt-5">
        <h1 className="text-center text-primary mb-4">Market Trend News</h1>

        {/* Search Bar */}
        <Form className="d-flex mb-4 justify-content-center">
            <Form.Control
            type="text"
            placeholder="Enter a topic (e.g. AI, Finance, Stocks)..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="w-50 me-2"
            />
            <Button variant="primary" onClick={handleSearch}>
            Search
            </Button>
        </Form>

        {error && <Alert variant="danger" className="text-center">{error}</Alert>}
        {loading && <div className="text-center"><Spinner animation="border" /></div>}

    {/* News Cards */}
        <Row>
            {news.length > 0 ? (
            news.map((article, index) => (
                <Col key={index} sm={12} md={6} lg={4}>
                <NewsCard {...article} />
                </Col>
            ))
            ) : (
            !loading && <p className="text-center text-muted">No news found. Try another keyword.</p>
            )}
        </Row>
    </Container>
    );
};

export default Home;
