// src/pages/About.js
import React from 'react';
import '../custom.css';

const FeatureCard = ({ icon, title, description }) => (
  <div className="bg-zinc-900 border border-zinc-800 rounded-lg p-6 hover:border-orange-500 transition-all duration-300">
    <div className="flex flex-col items-center text-center">
      <div className="text-orange-500 text-4xl mb-4">{icon}</div>
      <h3 className="text-xl font-semibold mb-2 text-white">{title}</h3>
      <p className="text-zinc-400">{description}</p>
    </div>
  </div>
);

const About = () => {
  const features = [
    {
      icon: "ðŸ§ ",
      title: "AI-Powered Analysis",
      description: "Leveraging IBM Granite AI models for advanced sentiment analysis and market trend predictions"
    },
    {
      icon: "ðŸ“ˆ",
      title: "Real-Time Insights",
      description: "Stay updated with live market news and sentiment analysis through our visual temperature meter"
    },
    {
      icon: "âš¡",
      title: "Smart Decisions",
      description: "Make data-driven business decisions with our AI-powered recommendations and insights"
    },
    {
      icon: "ðŸ‘¥",
      title: "Multiple Use Cases",
      description: "Perfect for analysts, startup founders, business owners, and VC firms seeking market intelligence"
    }
  ];

  return (
    <div className="min-h-screen bg-black text-white py-16 px-4">
      {/* Hero Section */}
      <div className="max-w-4xl mx-auto text-center mb-16">
        <h1 className="text-4xl md:text-5xl font-bold mb-6 bg-gradient-to-r from-orange-500 to-red-500 text-transparent bg-clip-text">
          Market Intelligence Reimagined
        </h1>
        <p className="text-xl text-zinc-400 mb-8">
          Welcome to the future of market analysis. Our AI-powered platform transforms complex market data into actionable insights.
        </p>
        <button className="bg-gradient-to-r from-orange-500 to-red-500 px-8 py-3 rounded-full font-semibold hover:opacity-90 transition-opacity flex items-center mx-auto gap-2">
          Get Started
          <span className="ml-2">â†’</span>
        </button>
      </div>

      {/* Features Grid */}
      <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-6 mb-16">
        {features.map((feature, index) => (
          <FeatureCard key={index} {...feature} />
        ))}
      </div>

      {/* Technology Stack */}
      <div className="max-w-4xl mx-auto text-center mb-16">
        <h2 className="text-3xl font-bold mb-8">Powered by Leading Technology</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {[
            "React Frontend",
            "Flask Backend",
            "IBM Granite AI",
            "Bing News API"
          ].map((tech, index) => (
            <div key={index} className="bg-zinc-900 rounded-lg p-4 text-center">
              <p className="text-zinc-300">{tech}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Call to Action */}
      <div className="max-w-2xl mx-auto text-center">
        <h2 className="text-2xl md:text-3xl font-bold mb-4">
          Ready to Transform Your Market Analysis?
        </h2>
        <p className="text-zinc-400 mb-8">
          Join industry leaders who trust our platform for their market intelligence needs.
        </p>
        <button className="bg-zinc-800 hover:bg-zinc-700 px-8 py-3 rounded-full font-semibold transition-colors">
          Learn More
        </button>
      </div>
    </div>
  );
};

export default About;
