import React from "react";
import { ProgressBar, OverlayTrigger, Tooltip } from "react-bootstrap";

const SentimentMeter = ({ score }) => {
    const getSentimentLabel = (score) => {
        if (score > 0.6) return "ðŸ”¥ Very Positive";
        if (score > 0.3) return "ðŸ˜Š Positive";
        if (score > -0.3) return "ðŸ˜ Neutral";
        if (score > -0.6) return "ðŸ˜• Negative";
        return "ðŸ’€ Very Negative";
    };

    const getVariant = (score) => {
        if (score > 0.6) return "success";
        if (score > 0.3) return "info";
        if (score > -0.3) return "secondary";
        if (score > -0.6) return "warning";
        return "danger";
    };

    return (
        <div className="sentiment-meter">
            <OverlayTrigger
                placement="top"
                overlay={<Tooltip>{getSentimentLabel(score)}</Tooltip>}
            >
                <ProgressBar now={(score + 1) * 50} variant={getVariant(score)} animated />
            </OverlayTrigger>
            <p className="mt-2 text-white">{getSentimentLabel(score)}</p>
        </div>
    );
};

export default SentimentMeter;
