import React from "react";
import { Gauge } from "recharts"; // Assuming you use recharts for the gauge

const SentimentMeter = ({ score }) => {
    const gaugeValue = (score + 1) * 50; // Convert [-1, 1] to [0, 100]
    
    return (
        <div className="sentiment-meter">
            <h5 className="text-white">Sentiment Score</h5>
            <Gauge value={gaugeValue} min={0} max={100} width={200} height={150} />
            <p className="mt-2 text-white">{score > 0.6 ? "ðŸ”¥ Very Positive" :
                score > 0.3 ? "ðŸ˜Š Positive" :
                score > -0.3 ? "ðŸ˜ Neutral" :
                score > -0.6 ? "ðŸ˜• Negative" : "ðŸ’€ Very Negative"}
            </p>
        </div>
    );
};

export default SentimentMeter;
