import React from "react";
import { Card, Button } from "react-bootstrap";

const NewsCard = ({ title, description, url, source, image, published_date }) => {
    return (
        <div className="col-md-4 mb-4">
            <Card className="h-100 shadow-lg" style={{ border: 'none', borderRadius: '15px', overflow: 'hidden' }}>
                {image && (
                    <Card.Img 
                        variant="top" 
                        src={image} 
                        className="card-img-top"
                        style={{ height: '200px', objectFit: 'cover', borderBottom: '1px solid rgba(0, 0, 0, 0.1)' }}
                    />
                )}
                <Card.Body className="d-flex flex-column p-4">
                    <Card.Title className="mb-3" style={{ fontSize: '1.25rem', fontWeight: '600', color: '#333' }}>
                        {title}
                    </Card.Title>
                    <Card.Text className="mb-4 flex-grow-1" style={{ fontSize: '0.9rem', color: '#666' }}>
                        {description}
                    </Card.Text>
                    <div className="d-flex justify-content-between align-items-center">
                        <div>
                            <Card.Text className="text-muted small mb-1">
                                Published: {new Date(published_date).toLocaleDateString()}
                            </Card.Text>
                            <Card.Text className="text-muted small">
                                Source: {source}
                            </Card.Text>
                        </div>
                        <Button 
                            variant="primary" 
                            href={url} 
                            target="_blank"
                            style={{ backgroundColor: '#eb5d3a', border: 'none', borderRadius: '25px', padding: '0.5rem 1.5rem' }}
                        >
                            Read More
                        </Button>
                    </div>
                </Card.Body>
            </Card>
        </div>
    );
};

export default NewsCard;
